C++<pre><code>class Solution {
public:
    long long countGood(vector&lt;int&gt;&amp; nums, int k) {
        unordered_map&lt;int, int&gt; freq;
        long long res = 0;
        int left = 0;
        long long current_pairs = 0;

        for (int right = 0; right &lt; nums.size(); ++right) {
            // ​增加​右​指针​对应​的​元素​，​并​更新​当前​的​对数​
            // ​每​增加​一个​nums[right]，​它会​和​之前​所有​的​nums[right]​形成​新​的​对​
            current_pairs += freq[nums[right]];
            freq[nums[right]]++;

            // ​当​当前​对数​ &gt;=k ​时​，​移动​左​指针​，​直到​对数​小于​k
            while (current_pairs &gt;= k) {
                // ​左​指针​移动​时​，​需要​减少​nums[left]​的​计数​，​并​更新​对数​
                // ​减少​一个​nums[left]，​之前​它​和​所有​其他​nums[left]​形成​的​对​需要​减去​
                freq[nums[left]]--;
                current_pairs -= freq[nums[left]];
                left++;
            }

            // ​每次​左​指针​移动​前​的​left​值​表示​以​right​结尾​的​满足条件​的​最​小子​数组​的​起始​位置​
            // ​所有​left​左边​的​起始​位置​到​right​的​子​数组​都​满足条件​
            res += left;
        }

        return res;
    }
};
</code></pre>

