C++<pre><code>class Solution {
private:
    int mergeSort(vector&lt;int&gt;&amp; record, int left, int right) {
        // ​递归​终止​条件​
        if (left &gt;= right) return 0;

        // ​分治​：​划分​区间​
        int mid = left + (right - left) / 2;
        int count = mergeSort(record, left, mid) + 
                    mergeSort(record, mid + 1, right);

        // ​合并​过程​：​统计​逆序​对​
        vector&lt;int&gt; temp(right - left + 1);
        int i = left, j = mid + 1, k = 0;

        while (i &lt;= mid &amp;&amp; j &lt;= right) {
            // ​如果​左边​元素​大于​右边​元素​，​则​形成​逆序​对​
            if (record[i] &gt; record[j]) {
                // ​重点​：​当前​右边​元素​与​左边​所有​剩余​元素​都​可以​形成​逆序​对​
                count += mid - i + 1;
                temp[k++] = record[j++];
            } else {
                temp[k++] = record[i++];
            }
        }

        // ​处理​剩余​元素​
        while (i &lt;= mid) temp[k++] = record[i++];
        while (j &lt;= right) temp[k++] = record[j++];

        // ​将​排序​后​的​结果​复制​回原​数组​
        for (int p = 0; p &lt; k; p++) {
            record[left + p] = temp[p];
        }

        return count;
    }

public:
    int reversePairs(vector&lt;int&gt;&amp; record) {
        // ​边界​处理​
        if (record.empty()) return 0;

        // ​调用​归并​排序​进行​逆序​对​统计​
        return mergeSort(record, 0, record.size() - 1);
    }
};
</code></pre>

C++<pre><code>class Solution {
private:
    // ​树状​数组​基本操作​
    vector&lt;int&gt; tree;

    void update(int index, int val) {
        while (index &lt; tree.size()) {
            tree[index] += val;
            index += index &amp; (-index);
        }
    }

    int query(int index) {
        int sum = 0;
        while (index &gt; 0) {
            sum += tree[index];
            index -= index &amp; (-index);
        }
        return sum;
    }

public:
    int reversePairs(vector&lt;int&gt;&amp; record) {
        if (record.empty()) return 0;

        // 1. ​离散​化​处理​
        vector&lt;int&gt; sorted = record;
        sort(sorted.begin(), sorted.end());
        sorted.erase(unique(sorted.begin(), sorted.end()), sorted.end());

        // 2. ​初始化​树状​数组​
        tree.resize(sorted.size() + 1, 0);

        // 3. ​统计​逆序​对​
        int result = 0;
        for (int i = record.size() - 1; i &gt;= 0; i--) {
            // ​找到​当前​元素​在​排序​后​数组​中​的​排名​
            int rank = lower_bound(sorted.begin(), sorted.end(), record[i]) - sorted.begin() + 1;

            // ​查询​比​当前​元素​小​的​元素​个数​
            result += query(rank - 1);

            // ​更新​树状​数组​
            update(rank, 1);
        }

        return result;
    }
};
</code></pre>

