C++<pre><code>#include &lt;iostream&gt;
#include &lt;vector&gt;
#include &lt;algorithm&gt;
using namespace std;

const int MAXN = 105;
int n, m;
bool graph[MAXN][MAXN] = {false}; // ​邻接矩阵​
int color[MAXN] = {0}; // ​颜色​数组​
int minColors; // ​最少​颜色​数​

// ​贪心​算法​（Welsh-Powell）​获取​初始​上​界​
int greedyColoring() {
    vector&lt;pair&lt;int, int&gt;&gt; degrees; // ​存储​顶点​及其​度数​
    for (int i = 1; i &lt;= n; ++i) {
        int degree = 0;
        for (int j = 1; j &lt;= n; ++j) {
            if (graph[i][j]) degree++;
        }
        degrees.emplace_back(degree, i);
    }
    // ​按​度数​从大到​小​排序​
    sort(degrees.begin(), degrees.end(), greater&lt;pair&lt;int, int&gt;&gt;());

    int usedColors = 0;
    vector&lt;bool&gt; available(n + 1, true); // ​颜色​可用性​
    for (const auto&amp; p : degrees) {
        int person = p.second;
        // ​初始化​所有​颜色​可用​
        fill(available.begin(), available.end(), true);
        // ​检查​相邻​顶点​的​颜色​
        for (int i = 1; i &lt;= n; ++i) {
            if (graph[person][i] &amp;&amp; color[i] != 0) {
                available[color[i]] = false;
            }
        }
        // ​分配​最小​可用​颜色​
        int c;
        for (c = 1; c &lt;= n; ++c) {
            if (available[c]) break;
        }
        color[person] = c;
        usedColors = max(usedColors, c);
    }
    return usedColors;
}

// ​检查​第​person​个人​是否​可以​染成​c​颜色​
bool isSafe(int person, int c) {
    for (int i = 1; i &lt;= n; ++i) {
        if (graph[person][i] &amp;&amp; color[i] == c) {
            return false;
        }
    }
    return true;
}

// ​回溯​法​尝试​为​第​person​个人​分配​颜色​
void backtrack(int person, int usedColors) {
    if (person &gt; n) {
        minColors = min(minColors, usedColors);
        return;
    }
    if (usedColors &gt;= minColors) {
        return;
    }
    for (int c = 1; c &lt;= usedColors + 1; ++c) {
        if (c &gt; minColors) break;
        if (isSafe(person, c)) {
            color[person] = c;
            int newUsedColors = max(usedColors, c);
            backtrack(person + 1, newUsedColors);
            color[person] = 0; // ​回溯​
        }
    }
}

int main() {
    cin &gt;&gt; n &gt;&gt; m;
    for (int i = 0; i &lt; m; ++i) {
        int a, b;
        cin &gt;&gt; a &gt;&gt; b;
        graph[a][b] = graph[b][a] = true;
    }
    // ​使用​贪心​算法​获取​初始​上​界​
    minColors = greedyColoring();
    // ​重置​color​数组​
    fill(color, color + MAXN, 0);
    // ​回溯​法​优化​
    backtrack(1, 0);
    cout &lt;&lt; minColors &lt;&lt; endl;
    return 0;
}
</code></pre>

C++<pre><code>#include &lt;iostream&gt;
#include &lt;queue&gt;
#include &lt;deque&gt;
#include &lt;unordered_set&gt;
#include &lt;algorithm&gt;
using namespace std;

int main() {
    string strA, strB;
    cin &gt;&gt; strA &gt;&gt; strB;

    queue&lt;char&gt; qA, qB; // ​玩家​A​和​B​的​牌​队列​
    deque&lt;char&gt; table;   // ​桌面上​的​牌​，​用​双端​队列​便于​两端​操作​

    // ​初始化​玩家​A​的​牌​
    for (char c : strA) {
        qA.push(c);
    }
    // ​初始化​玩家​B​的​牌​
    for (char c : strB) {
        qB.push(c);
    }

    int turn = 0; // 0​表示​A​的​回合​，1​表示​B​的​回合​
    int noWinCount = 0; // ​连续​未​赢牌​的​次数​
    const int maxTurns = 100000; // ​最大​回合​数​限制​，​防止​无限​循环​

    for (int i = 0; i &lt; maxTurns; ++i) {
        // ​检查​游戏​是否​应该​结束​
        if (qA.empty() || qB.empty()) {
            break;
        }
        char card;
        if (turn == 0) { // A​的​回合​
            card = qA.front();
            qA.pop();
            table.push_front(card); // A​出​牌​放到​桌面​最​上面​
        }
        else { // B​的​回合​
            card = qB.front();
            qB.pop();
            table.push_front(card); // B​出​牌​放到​桌面​最​上面​
        }

        // ​检查​是否​有​匹配​的​牌​
        auto it = find(table.begin() + 1, table.end(), card);
        if (it != table.end()) { // ​找到​匹配​的​牌​
            if (turn == 0) { // A​赢牌​
                // ​将​匹配​的​牌​及其​之间​的​牌​加入​A​的​队列​尾部​（​逆序​）
                int dist = distance(table.begin(), it);
                for (int j = 0; j &lt;= dist; ++j) {
                    qA.push(table.front());
                    table.pop_front();
                }
            }
            else { // B​赢牌​
                // ​将​匹配​的​牌​及其​之间​的​牌​加入​B​的​队列​尾部​（​逆序​）
                int dist = distance(table.begin(), it);
                for (int j = 0; j &lt;= dist; ++j) {
                    qB.push(table.front());
                    table.pop_front();
                }
            }
            // ​赢牌​的​一方​继续​出​牌​，​不​需要​切换​回合​
        }
        else {
            // ​没有​赢牌​，​切换​到​对方​回合​
            turn = 1 - turn;
            noWinCount++; // ​连续​未​赢牌​次数​+1
            // ​如果​连续​未​赢牌​次数​超过​10000，​则​游戏​结束​
            if (noWinCount &gt;= 10000) {
                cout &lt;&lt; -1 &lt;&lt; endl;
                return 0;
            }   
        }
    }

    // ​游戏​结束​，​输出​赢家​的​牌​
    if (qA.empty()) {
        // B​赢​了​，​输出​B​的​牌​
        while (!qB.empty()) {
            cout &lt;&lt; qB.front();
            qB.pop();
        }
    }
    else {
        // A​赢​了​，​输出​A​的​牌​
        while (!qA.empty()) {
            cout &lt;&lt; qA.front();
            qA.pop();
        }
    }
    cout &lt;&lt; endl;

    return 0;
}
</code></pre> <p>​循环​中​对​容器​大小​做​修改​时​，​循环​条件​中​不能​出现​容器​大小​相关​的​临界​条件​，​因为​容器​大小​会​随着​循环​进行​不断​变化​。</p> <p>​为什么​桌面​牌要​用​双端​队列​来​模拟​，​普通​队列​不​可以​吗​？​因为​双端​队列​可以​方便​地​在​队头​、​队尾​两侧​添加​和​删除​元素​，​以​上述​代码​中​地​模拟​方式​为例​，​将​A​和​B​出​的​牌​依次​加到​队头​，​如果​某次​出牌​在​桌面​牌中​找到​重复​牌​，​则​可以​使用​pop_front()​方法​依次​弹​出​桌面​牌​，​而且​是​按照​游戏规则​，​逆序​地​将​桌面​重复​牌​加入​到​获胜​玩家​的​手牌​中​。​如果​使用​普通​队列​，​则​每次​A​和​B​出​的​牌​都​只能​添加​到队​尾​，​此时​如果​某次​出牌​在​桌面​牌中​找到​了​重复​牌​，​重复​牌​区间​为队​中​某个​元素​到队​尾​，​普通​队列​无法​直接​获取​队尾​元素​，​无法​将​重复​牌​取出​加​到​获胜​玩家​的​手牌​中​，​必须​使用​一个​辅助​栈来​临时​存储​普通​队列​中​的​元素​，</p> <ol> <li>​每次​出牌​时​，​用​<code>push()</code>​放入​牌堆​后​端​（​相当于​牌堆​的​"​底部​"）</li> <li>​检查​匹配​时​需要​：</li> <li>​临时​弹​出​所有​牌到​辅助​栈中​，​检查​是否​有​匹配​</li> <li>​如果​没有​匹配​，​再放​回牌​堆​</li> <li>​如果​找到​匹配​，​则​逆序​处理​赢取​的​牌​</li> </ol> <p>​但​这样​实现​会​非常​低效​，​因为​每次​出牌​都​需要​遍历​整个​牌堆​。</p> <p>​检测​游戏​是否​进入​无限​循环​的​原理​？​如果​持续​超过​10000​个​回合​，​还是​没有​分出​胜负​，​就​认为​进入​了​无限​循环​</p> C++<pre><code>#include &lt;bits/stdc++.h&gt;    // ​作用​是​包含​几乎​所有​标准​ C++ ​库​的​头文件​
using namespace std; 
bool a[128];        // a[i]​表示​牌堆​中​是否​存在​i​这​张牌​ 

int main() { 
    string A,B; 
    cin&gt;&gt;A&gt;&gt;B; 
    stack&lt;char&gt; S;                          // ​用栈​作为​牌堆​
    S.push(A[0]); a[A[0]]=1; A.erase(0,1); // A​先出​牌​
    bool flag=1; // flag​控制​到​谁​出牌​ 
    int times=0; // times​表示​出​牌​次数​，​超过​10000​认为​会​无限​循环​ 
    while(A.length() &amp;&amp; B.length() &amp;&amp; times&lt;10000){ 
        //cout&lt;&lt;A&lt;&lt;","&lt;&lt;B&lt;&lt;endl; 
        string* sp=flag?&amp;B:&amp;A; // flag​为​1​时​B​出​牌​，​将​string​指针​指向​B，​方便​实现​B​的​出牌​和​收牌​ 
        char tmp=(*sp)[0];
        S.push(tmp); sp-&gt;erase(0,1); // ​玩家​出​牌​ 
        if(a[tmp]==0) { a[tmp]=1; flag = !flag; } // ​牌堆​中​没有​当前​出​的​牌​，​牌权​更换​ 
        else{ // ​若​包含​当前​字符​，​收回​一部分​牌​ 
            *sp += S.top(); S.pop(); // ​收回​刚出​的​那​张牌​，​位于​栈顶​ 
            while(S.top()!=tmp){ *sp += S.top(); a[S.top()] = 0; S.pop(); } //​一直​收牌​到​与​所​出牌​相同​的​另​一张​牌处​ 
            *sp += S.top(); a[S.top()] = 0; S.pop(); 
        } 
        times++; 
    } 
    if(times&gt;=10000) return -1; 
    if(A.length()) cout&lt;&lt;A; else cout&lt;&lt;B; 
    return 0; 
}
</code></pre>

Text Only<pre><code>​插入​：​在​word1​中​插入​word2​的​第​j​个字符​  
​状态​含义​：​在​word1​末尾​插入​一个​字符​，​使​其​与​word2​前​j​个字符​匹配​  
​操作​代价​：1（​插入​新​字符​）  
</code></pre> <p>​示例​：</p> <ul> <li>"abc" -&gt; "abcd"</li> <li>​插入​'d'</li> <li>​此时​dp[i][j-1]​代表​插入​当前​字符​后​的​最小​操作数​</li> </ul> C++<pre><code>class Solution {  
public:  
    int minDistance(string word1, string word2) {  
        // ​获取​两个​单词​的​长度​  
        int m = word1.length();  
        int n = word2.length();  

        // ​创建​二维​动态​规划​数组​  
        // dp[i][j]​表示​word1​的​前​i​个字符​转换​到​word2​的​前​j​个字符​所​需​的​最少​操作数​  
        vector&lt;vector&lt;int&gt;&gt; dp(m + 1, vector&lt;int&gt;(n + 1, 0));  

        // ​初始化​边界条件​  
        // ​第一行​：word1​为空​，​需要​插入​word2​的​所有​字符​  
        for (int j = 1; j &lt;= n; j++) {  
            dp[0][j] = j;  
        }  

        // ​第一列​：word2​为空​，​需要​删除​word1​的​所有​字符​  
        for (int i = 1; i &lt;= m; i++) {  
            dp[i][0] = i;  
        }  

        // ​动态​规划​填表​  
        for (int i = 1; i &lt;= m; i++) {  
            for (int j = 1; j &lt;= n; j++) {  
                // ​如果​当前​字符​相同​，​不​需要​操作​  
                if (word1[i-1] == word2[j-1]) {  
                    dp[i][j] = dp[i-1][j-1];  
                }  
                else {  
                    // ​取​三种​操作​中​最小​的​代价​：  
                    // 1. ​替换​字符​（​替换​最后​一个​字符​）  
                    // 2. ​删除​字符​（​删除​word1​最后​一个​字符​）  
                    // 3. ​插入​字符​（​在​word1​末尾​插入​字符​）  
                    dp[i][j] = 1 + min({  
                        dp[i-1][j-1],  // ​替换​  
                        dp[i-1][j],    // ​删除​  
                        dp[i][j-1]     // ​插入​  
                    });  
                }  
            }  
        }  

        // ​返回​将​word1​转换​为​word2​所​需​的​最少​操作数​  
        return dp[m][n];  
    }  
};  
</code></pre> <p>​对于​编辑​距离​问题​，​除了​动态​规划​，​还有​几种​解决​方法​：</p> <ol> <li>​递归​（​回溯​）​解法​ ​优点​：​思路​简单​ ​缺点​：​时间​复杂度​指数​级​O(3^m)，​会​超时​</li> </ol> C++<pre><code>class Solution {
public:
    int minDistance(string word1, string word2) {
        return dfs(word1, word2, 0, 0);
    }

private:
    int dfs(string&amp; word1, string&amp; word2, int i, int j) {
        // ​递归​终止​条件​
        if (i == word1.length()) return word2.length() - j;
        if (j == word2.length()) return word1.length() - i;

        // ​字符​相同​，​不​需要​操作​
        if (word1[i] == word2[j]) 
            return dfs(word1, word2, i+1, j+1);

        // ​三种​操作​的​最小值​
        return 1 + min({
            dfs(word1, word2, i+1, j),   // ​删除​
            dfs(word1, word2, i, j+1),   // ​插入​
            dfs(word1, word2, i+1, j+1)  // ​替换​
        });
    }
};
</code></pre> <ol> <li>​记忆​化​搜索​（​递归​+​备忘录​） ​优点​：​避免​重复​计算​ ​时间​复杂度​：O(mn) ​空间​复杂度​：O(mn)</li> </ol> C++<pre><code>class Solution {
public:
    int minDistance(string word1, string word2) {
        memo = vector&lt;vector&lt;int&gt;&gt;(word1.length(), 
                   vector&lt;int&gt;(word2.length(), -1));
        return dfs(word1, word2, 0, 0);
    }

private:
    vector&lt;vector&lt;int&gt;&gt; memo;

    int dfs(string&amp; word1, string&amp; word2, int i, int j) {
        // ​递归​终止​条件​
        if (i == word1.length()) return word2.length() - j;
        if (j == word2.length()) return word1.length() - i;

        // ​检查​备忘录​
        if (memo[i][j] != -1) return memo[i][j];

        // ​字符​相同​，​不​需要​操作​
        if (word1[i] == word2[j]) {
            memo[i][j] = dfs(word1, word2, i+1, j+1);
            return memo[i][j];
        }

        // ​三种​操作​的​最小值​
        memo[i][j] = 1 + min({
            dfs(word1, word2, i+1, j),   // ​删除​
            dfs(word1, word2, i, j+1),   // ​插入​
            dfs(word1, word2, i+1, j+1)  // ​替换​
        });

        return memo[i][j];
    }
};
</code></pre> <ol> <li>​空间​优化​的​动态​规划​ ​优点​：​空间​复杂度​降低​到​O(min(m,n))</li> </ol> C++<pre><code>class Solution {
public:
    int minDistance(string word1, string word2) {
        // ​交换​，​确保​word1​是​较​短​的​字符串​
        if (word1.length() &gt; word2.length()) 
            swap(word1, word2);

        int m = word1.length(), n = word2.length();
        vector&lt;int&gt; pre(m + 1, 0);
        vector&lt;int&gt; cur(m + 1, 0);

        // ​初始化​第一行​
        for (int j = 0; j &lt;= m; j++) 
            pre[j] = j;

        // ​逐行​计算​
        for (int i = 1; i &lt;= n; i++) {
            cur[0] = i;
            for (int j = 1; j &lt;= m; j++) {
                if (word2[i-1] == word1[j-1]) 
                    cur[j] = pre[j-1];
                else 
                    cur[j] = 1 + min({pre[j-1], pre[j], cur[j-1]});
            }
            pre = cur;
        }

        return pre[m];
    }
};
</code></pre> <ol> <li>​位​运算​优化​（​适用​特殊​场景​） ​对于​较​短​字符串​，​可以​使用​位​运算​加速​</li> </ol> C++<pre><code>class Solution {
public:
    int minDistance(string word1, string word2) {
        int m = word1.length(), n = word2.length();

        // ​如果​字符串​很​短​（&lt;32），​可以​使用​位​运算​
        if (m &lt;= 32 &amp;&amp; n &lt;= 32) {
            return bitOptimizedDistance(word1, word2);
        }

        // ​回退​到​标准​动态​规划​
        return standardDP(word1, word2);
    }

private:
    int bitOptimizedDistance(string&amp; word1, string&amp; word2) {
        // ​位​运算​实现​，​仅​适用​于​短​字符串​
        // ​具体​实现​略​（​需要​复杂​的​位操作​）
    }

    int standardDP(string&amp; word1, string&amp; word2) {
        // ​标准​动态​规划​实现​
        vector&lt;vector&lt;int&gt;&gt; dp(word1.length() + 1, 
                               vector&lt;int&gt;(word2.length() + 1, 0));
        // ​动态​规划​逻辑​
    }
};
</code></pre>

C++<pre><code>class Solution {
private:
    const int N = 110, M = 6010;
    vector&lt;int&gt; he, e, ne, w;
    vector&lt;int&gt; dist;
    vector&lt;bool&gt; vis;
    const int INF = 0x3f3f3f3f;
    int n, k, idx = 0;

    // ​添加​边​
    void add(int a, int b, int c) {
        e.push_back(b);
        ne.push_back(he[a]);
        he[a] = idx;
        w.push_back(c);
        idx++;
    }

public:
    int networkDelayTime(vector&lt;vector&lt;int&gt;&gt;&amp; ts, int n, int k) {
        // ​初始化​邻接​表​
        he = vector&lt;int&gt;(n + 1, -1);
        e.clear();
        ne.clear();
        w.clear();
        idx = 0;

        // ​添加​边​
        for (const auto&amp; t : ts) {
            int u = t[0], v = t[1], c = t[2];
            add(u, v, c);
        }

        // ​执行​SPFA​算法​
        spfa();

        // ​找出​最大​最​短距离​
        int ans = 0;
        for (int i = 1; i &lt;= n; i++) {
            ans = max(ans, dist[i]);
        }

        return ans &gt; INF / 2 ? -1 : ans;
    }

private:
    void spfa() {
        // ​初始化​距离​和​访问​数组​
        dist = vector&lt;int&gt;(n + 1, INF);
        vis = vector&lt;bool&gt;(n + 1, false);

        // ​起始​节点​距离​设​为​0
        dist[k] = 0;

        // ​使用​队列​进行​松弛​
        deque&lt;int&gt; d;
        d.push_back(k);
        vis[k] = true;

        while (!d.empty()) {
            // ​取出​队首​节点​
            int poll = d.front();
            d.pop_front();
            vis[poll] = false;

            // ​遍历​当前​节点​的​所有​邻接​节点​
            for (int i = he[poll]; i != -1; i = ne[i]) {
                int j = e[i];

                // ​尝试​松弛​边​
                if (dist[j] &gt; dist[poll] + w[i]) {
                    dist[j] = dist[poll] + w[i];

                    // ​如果​节点​已​在​队列​中​，​跳过​
                    if (vis[j]) continue;

                    // ​将​节点​加入​队列​
                    d.push_back(j);
                    vis[j] = true;
                }
            }
        }
    }
};
</code></pre> <p>​基础知识​： Bellman-Ford ​算法​（​最短​路径​，​解决​负权​边​，​检测​负环​回路​）+ ​队列​优化​</p> C++<pre><code>#include &lt;iostream&gt;  
#include &lt;vector&gt;  
#include &lt;limits&gt;  
#include &lt;algorithm&gt;  

// ​定义​边​的​结构​体​  
struct Edge {  
    int src;    // ​起点​  
    int dest;   // ​终点​  
    int weight; // ​权重​  
};  

class BellmanFord {  
private:  
    int V;              // ​顶点​数​  
    std::vector&lt;Edge&gt; edges; // ​边​的​集合​  

public:  
    // ​构造函数​  
    BellmanFord(int vertices) : V(vertices) {}  

    // ​添加​边​  
    void addEdge(int src, int dest, int weight) {  
        edges.push_back({src, dest, weight});  
    }  

    // Bellman-Ford​算法​主体​  
    std::pair&lt;bool, std::vector&lt;int&gt;&gt; findShortestPaths(int source) {  
        // ​距离​数组​，​初始化​为​正​无穷​  
        std::vector&lt;int&gt; dist(V, std::numeric_limits&lt;int&gt;::max());  

        // ​源点​距离​为​0  
        dist[source] = 0;  

        // ​记录​前驱​节点​的​数组​  
        std::vector&lt;int&gt; predecessor(V, -1);  

        // ​松弛​操作​V-1​次​  
        for (int i = 1; i &lt;= V - 1; ++i) {  
            bool updated = false;  

            // ​遍历​所有​边​进行​松弛​  
            for (const auto&amp; edge : edges) {  
                int u = edge.src;  
                int v = edge.dest;  
                int weight = edge.weight;  

                // ​避免​整数​溢出​  
                if (dist[u] != std::numeric_limits&lt;int&gt;::max() &amp;&amp;  
                    dist[u] + weight &lt; dist[v]) {  
                    dist[v] = dist[u] + weight;  
                    predecessor[v] = u;  
                    updated = true;  
                }  
            }  

            // ​如果​没有​更新​，​提前​退出​  
            if (!updated) break;  
        }  

        // ​检测​负权​环​  
        for (const auto&amp; edge : edges) {  
            int u = edge.src;  
            int v = edge.dest;  
            int weight = edge.weight;  

            // ​如果​还​能​继续​松弛​，​说明​存在​负权​环​  
            if (dist[u] != std::numeric_limits&lt;int&gt;::max() &amp;&amp;  
                dist[u] + weight &lt; dist[v]) {  
                return {false, {}}; // ​存在​负权​环​  
            }  
        }  

        return {true, dist};  
    }  

    // ​重建​最短​路径​  
    std::vector&lt;int&gt; getPath(const std::vector&lt;int&gt;&amp; predecessor, int target) {  
        std::vector&lt;int&gt; path;  
        for (int v = target; v != -1; v = predecessor[v]) {  
            path.push_back(v);  
        }  
        std::reverse(path.begin(), path.end());  
        return path;  
    }  
};  

// ​主​函数​示例​  
int main() {  
    // ​创建​图​：5​个​顶点​  
    BellmanFord graph(5);  

    // ​添加​边​   
    // 0: A, 1: B, 2: C, 3: D, 4: E  
    graph.addEdge(0, 1, 3);  // A -&gt; B, ​权重​3  
    graph.addEdge(0, 2, 2);  // A -&gt; C, ​权重​2  
    graph.addEdge(1, 3, 4);  // B -&gt; D, ​权重​4  
    graph.addEdge(3, 2, -1); // D -&gt; C, ​权重​-1  

    // ​以​A(0)​为​源点​求​最​短​路径​  
    int source = 0;  
    auto [success, distances] = graph.findShortestPaths(source);  

    if (success) {  
        std::cout &lt;&lt; "​最短​路径​距离​:\n";  
        for (int i = 0; i &lt; distances.size(); ++i) {  
            std::cout &lt;&lt; "​从​源点​到​顶点​ " &lt;&lt; i &lt;&lt; ": "   
                      &lt;&lt; (distances[i] == std::numeric_limits&lt;int&gt;::max() ?   
                          "​不可​达​" : std::to_string(distances[i])) &lt;&lt; "\n";  
        }  
    } else {  
        std::cout &lt;&lt; "​图中​存在​负权​环​\n";  
    }  

    return 0;  
}  
</code></pre>

