C++<pre><code>cppclass Solution {  
public:  
    double myPow(double x, int n) {  
        // long long ​处理​ INT_MIN  
        long long N = n;  

        // ​负数​指数​处理​  
        if (N &lt; 0) {  
            x = 1 / x;  
            N = -N;  
        }  

        double result = 1.0;  

        // ​迭代​快速​幂​  
        while (N &gt; 0) {  
            // ​当前​位为​1，​乘入​结果​  
            if (N &amp; 1) {  
                result *= x;  
            }  

            // ​底数​平方​  
            x *= x;  

            // ​指数​右移​  
            N &gt;&gt;= 1;  
        }  

        return result;  
    }  
};  
</code></pre>

