C++<pre><code>#include &lt;iostream&gt;
#include &lt;vector&gt;

/**
 * 插入排序函数
 * @param arr 待排序的数组（使用vector容器）
 */
void insertionSort(std::vector&lt;int&gt;&amp; arr) {
    // 获取数组长度
    int n = arr.size();

    // 从第二个元素开始遍历（i=1），因为第一个元素默认已排序
    for (int i = 1; i &lt; n; i++) {
        // 保存当前需要插入的元素值（防止被覆盖）
        int key = arr[i];

        // j表示已排序部分的最后一个元素的索引
        int j = i - 1;

        /* 将当前元素(key)与已排序部分的元素从后向前比较
         * 如果已排序元素大于key，则将该元素后移一位
         * 直到找到key的正确位置或到达数组开头
         */
        while (j &gt;= 0 &amp;&amp; key &lt; arr[j]) {
            arr[j + 1] = arr[j]; // 将arr[j]元素后移
            j--;                 // 继续向前比较
        }

        // 将key插入到正确的位置（j+1是因为循环结束时j可能减1过多）
        arr[j + 1] = key;

        // 可选：打印每轮排序后的数组状态（用于理解算法过程）
        /*
        std::cout &lt;&lt; "第" &lt;&lt; i &lt;&lt; "轮排序后: ";
        for (int num : arr) {
            std::cout &lt;&lt; num &lt;&lt; " ";
        }
        std::cout &lt;&lt; std::endl;
        */
    }
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {12, 11, 13, 5, 6};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用插入排序函数
    insertionSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
