C++<pre><code>#include &lt;iostream&gt;
#include &lt;vector&gt;

/**
 * 希尔排序函数
 * @param arr 待排序的数组（使用vector容器）
 */
void shellSort(std::vector&lt;int&gt;&amp; arr) {
    // 获取数组长度
    int n = arr.size();

    // 初始增量gap设置为数组长度的一半，然后逐步缩小gap
    for (int gap = n/2; gap &gt; 0; gap /= 2) {
        // 从gap位置开始，对每个子序列进行插入排序
        for (int i = gap; i &lt; n; i++) {
            // 保存当前需要插入的元素值
            int temp = arr[i];

            // j表示当前元素在子序列中的前一个元素的索引
            int j;

            /* 对子序列进行插入排序
             * 将当前元素与子序列中前面的元素比较
             * 如果前面的元素较大，则后移
             */
            for (j = i; j &gt;= gap &amp;&amp; arr[j - gap] &gt; temp; j -= gap) {
                arr[j] = arr[j - gap];    // 当前元素i在子序列中的前一个元素，如果当前元素i在子序列中的前一个元素比元素i大，则继续向前（减去gap）寻找，直到找到比当前元素i小的，此时j的位置就是元素i在子序列中排序的正确位置
            }

            // 将temp（也就是arr[i]的值）插入到正确的位置
            arr[j] = temp;
        }

        // 可选：打印每轮排序后的数组状态（用于理解算法过程）
        /*
        std::cout &lt;&lt; "增量" &lt;&lt; gap &lt;&lt; "排序后: ";
        for (int num : arr) {
            std::cout &lt;&lt; num &lt;&lt; " ";
        }
        std::cout &lt;&lt; std::endl;
        */
    }
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {12, 34, 54, 2, 3, 8, 11, 25, 7};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用希尔排序函数
    shellSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
</code></pre>

C++<pre><code>#include &lt;iostream&gt;
#include &lt;vector&gt;
#include &lt;algorithm&gt; // 用于swap函数

/**
 * 分区函数 - 将数组分为两部分，返回基准元素的最终位置
 * @param arr 待排序数组
 * @param low 起始索引
 * @param high 结束索引
 * @return 基准元素的正确位置索引
 */
int partition(std::vector&lt;int&gt;&amp; arr, int low, int high) {
    // 选择最右边的元素作为基准(pivot)
    int pivot = arr[high];

    // i指向小于基准的子数组的最后一个元素
    int i = low - 1;

    // 遍历从low到high-1的所有元素
    for (int j = low; j &lt; high; j++) {
        // 如果当前元素小于或等于基准
        if (arr[j] &lt;= pivot) {  // 每找到一个比基准小的元素，就将i加1，然后将当前比基准小的元素arr[j]交换到左半部分，遍历结束后，比基准小的元素就全部在左半边，比基准大的元素自然就都到了右半边
            i++; // 扩大小于基准的子数组
            std::swap(arr[i], arr[j]); // 将当前元素arr[j]交换到小于基准的区域
        }
    }

    // 将基准元素交换到正确位置(i+1)
    std::swap(arr[i + 1], arr[high]);

    // 返回基准的最终位置
    return i + 1;
}

/**
 * 快速排序主函数
 * @param arr 待排序数组
 * @param low 起始索引
 * @param high 结束索引
 */
void quickSort(std::vector&lt;int&gt;&amp; arr, int low, int high) {
    if (low &lt; high) {
        // pi是分区索引，arr[pi]现在在正确位置
        int pi = partition(arr, low, high);

        // 递归排序分区前的子数组
        quickSort(arr, low, pi - 1);

        // 递归排序分区后的子数组
        quickSort(arr, pi + 1, high);
    }
}

/**
 * 快速排序的包装函数，简化调用
 * @param arr 待排序数组
 */
void quickSort(std::vector&lt;int&gt;&amp; arr) {
    quickSort(arr, 0, arr.size() - 1);
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {10, 7, 8, 9, 1, 5};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用快速排序函数
    quickSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
