C++<pre><code>#include &lt;iostream&gt;
#include &lt;vector&gt;

/**
 * 合并两个已排序的子数组
 * @param arr 原始数组
 * @param left 左子数组起始索引
 * @param mid 左子数组结束索引/右子数组起始索引-1
 * @param right 右子数组结束索引
 * @param temp 临时数组用于合并
 */
void merge(std::vector&lt;int&gt;&amp; arr, int left, int mid, int right, std::vector&lt;int&gt;&amp; temp) {
    int i = left;    // 左子数组起始位置
    int j = mid + 1; // 右子数组起始位置
    int k = 0;       // 临时数组索引

    // 比较两个子数组的元素，将较小的放入临时数组
    while (i &lt;= mid &amp;&amp; j &lt;= right) {
        if (arr[i] &lt;= arr[j]) {  // 保持稳定性（&lt;= 而不是 &lt;）
            temp[k++] = arr[i++];
        } else {
            temp[k++] = arr[j++];
        }
    }

    // 将左子数组剩余元素复制到临时数组
    while (i &lt;= mid) {
        temp[k++] = arr[i++];
    }

    // 将右子数组剩余元素复制到临时数组
    while (j &lt;= right) {
        temp[k++] = arr[j++];
    }

    // 将临时数组中的合并结果复制回原数组
    for (i = 0; i &lt; k; i++) {
        arr[left + i] = temp[i];
    }
}

/**
 * 归并排序递归函数
 * @param arr 待排序数组
 * @param left 当前子数组起始索引
 * @param right 当前子数组结束索引
 * @param temp 临时数组
 */
void mergeSort(std::vector&lt;int&gt;&amp; arr, int left, int right, std::vector&lt;int&gt;&amp; temp) {
    if (left &lt; right) {  // 子数组长度大于1时才需要排序
        int mid = left + (right - left) / 2;  // 防止溢出的中间位置计算

        // 递归排序左半部分
        mergeSort(arr, left, mid, temp);

        // 递归排序右半部分
        mergeSort(arr, mid + 1, right, temp);

        // 合并两个已排序的子数组
        merge(arr, left, mid, right, temp);
    }
}

/**
 * 归并排序的包装函数，简化调用
 * @param arr 待排序数组
 */
void mergeSort(std::vector&lt;int&gt;&amp; arr) {
    // 创建临时数组，避免在递归中重复创建
    std::vector&lt;int&gt; temp(arr.size());
    mergeSort(arr, 0, arr.size() - 1, temp);
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {12, 11, 13, 5, 6, 7};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用归并排序函数
    mergeSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
