/**
 * 堆化函数 - 维护最大堆性质
 * @param arr 待堆化的数组
 * @param n 堆的大小
 * @param i 当前需要堆化的节点索引
 */
// 将以索引为i的节点为根节点的子树转化为最大堆
void heapify(std::vector&lt;int&gt;&amp; arr, int n, int i) {
    int largest = i;       // 初始化最大值为当前节点
    int left = 2 * i + 1;  // 左子节点索引
    int right = 2 * i + 2; // 右子节点索引

    // 如果左子节点存在且大于当前最大值
    if (left &lt; n &amp;&amp; arr[left] &gt; arr[largest]) {
        largest = left;
    }

    // 如果右子节点存在且大于当前最大值
    if (right &lt; n &amp;&amp; arr[right] &gt; arr[largest]) {
        largest = right;
    }

    // 如果最大值不是当前节点
    if (largest != i) {
        std::swap(arr[i], arr[largest]); // 交换节点值
        heapify(arr, n, largest);        // 递归堆化受影响的子树
    }
}

/**
 * 堆排序主函数
 * @param arr 待排序数组
 */
void heapSort(std::vector&lt;int&gt;&amp; arr) {
    int n = arr.size();

    // 1. 构建最大堆（从最后一个非叶子节点开始）
    for (int i = n / 2 - 1; i &gt;= 0; i--) {
        heapify(arr, n, i);
    }

    // 2. 逐个提取堆顶元素（最大值）
    for (int i = n - 1; i &gt; 0; i--) {
        // 将当前堆顶（最大值）移到数组末尾
        std::swap(arr[0], arr[i]);

        // 对剩余元素重新堆化（堆大小减1）
        heapify(arr, i, 0);  // i为去除堆顶元素后当前堆的元素个数
    }
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {12, 11, 13, 5, 6, 7};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用堆排序函数
    heapSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
