// 二叉搜索树节点结构
struct TreeNode {
    int val;
    std::shared_ptr&lt;TreeNode&gt; left;
    std::shared_ptr&lt;TreeNode&gt; right;

    // 构造函数
    TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
};

/**
 * 向BST插入新节点（递归实现）
 * @param root 当前子树根节点
 * @param val 要插入的值
 * @return 插入后的子树根节点
 */
std::shared_ptr&lt;TreeNode&gt; insert(std::shared_ptr&lt;TreeNode&gt; root, int val) {
    // 如果当前子树为空，创建新节点
    if (!root) {
        return std::make_shared&lt;TreeNode&gt;(val);
    }

    // 递归插入左子树或右子树
    if (val &lt;= root-&gt;val) {  // 注意&lt;=保持稳定性（相同元素放左边）
        root-&gt;left = insert(root-&gt;left, val);
    } else {
        root-&gt;right = insert(root-&gt;right, val);
    }

    return root;
}

/**
 * 中序遍历BST并将结果存入数组
 * @param root 当前访问的节点
 * @param result 存储结果的数组
 */
void inOrderTraversal(const std::shared_ptr&lt;TreeNode&gt;&amp; root, std::vector&lt;int&gt;&amp; result) {
    if (!root) return;

    // 左子树 -&gt; 根节点 -&gt; 右子树
    inOrderTraversal(root-&gt;left, result);
    result.push_back(root-&gt;val);
    inOrderTraversal(root-&gt;right, result);
}

/**
 * 树形排序主函数
 * @param arr 待排序数组
 */
void treeSort(std::vector&lt;int&gt;&amp; arr) {
    std::shared_ptr&lt;TreeNode&gt; root = nullptr;

    // 1. 构建BST
    for (int num : arr) {
        root = insert(root, num);
    }

    // 2. 中序遍历获取排序结果
    arr.clear(); // 清空原数组
    inOrderTraversal(root, arr);
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {5, 3, 7, 2, 4, 6, 8};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用树形排序
    treeSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
