// 节点颜色枚举
enum class Color { RED, BLACK };

// 红黑树节点结构
struct RBTreeNode {
    int val;
    Color color;
    std::shared_ptr&lt;RBTreeNode&gt; left;
    std::shared_ptr&lt;RBTreeNode&gt; right;
    std::shared_ptr&lt;RBTreeNode&gt; parent;

    // 构造函数
    RBTreeNode(int x) : val(x), color(Color::RED), 
                       left(nullptr), right(nullptr), parent(nullptr) {}
};

// 红黑树类
class RBTree {
private:
    std::shared_ptr&lt;RBTreeNode&gt; root;
    std::shared_ptr&lt;RBTreeNode&gt; nil; // 哨兵节点，表示NIL叶子节点

    // 左旋
    void leftRotate(std::shared_ptr&lt;RBTreeNode&gt; x) {
        auto y = x-&gt;right;
        x-&gt;right = y-&gt;left;

        if (y-&gt;left != nil) {
            y-&gt;left-&gt;parent = x;
        }

        y-&gt;parent = x-&gt;parent;

        if (x-&gt;parent == nil) {
            root = y;
        } else if (x == x-&gt;parent-&gt;left) {
            x-&gt;parent-&gt;left = y;
        } else {
            x-&gt;parent-&gt;right = y;
        }

        y-&gt;left = x;
        x-&gt;parent = y;
    }

    // 右旋
    void rightRotate(std::shared_ptr&lt;RBTreeNode&gt; y) {
        auto x = y-&gt;left;
        y-&gt;left = x-&gt;right;

        if (x-&gt;right != nil) {
            x-&gt;right-&gt;parent = y;
        }

        x-&gt;parent = y-&gt;parent;

        if (y-&gt;parent == nil) {
            root = x;
        } else if (y == y-&gt;parent-&gt;right) {
            y-&gt;parent-&gt;right = x;
        } else {
            y-&gt;parent-&gt;left = x;
        }

        x-&gt;right = y;
        y-&gt;parent = x;
    }

    // 插入后修复红黑树性质
    void insertFixup(std::shared_ptr&lt;RBTreeNode&gt; z) {
        while (z-&gt;parent-&gt;color == Color::RED) {
            if (z-&gt;parent == z-&gt;parent-&gt;parent-&gt;left) {
                auto y = z-&gt;parent-&gt;parent-&gt;right;
                if (y-&gt;color == Color::RED) {
                    // 情况1：叔节点是红色
                    z-&gt;parent-&gt;color = Color::BLACK;
                    y-&gt;color = Color::BLACK;
                    z-&gt;parent-&gt;parent-&gt;color = Color::RED;
                    z = z-&gt;parent-&gt;parent;
                } else {
                    if (z == z-&gt;parent-&gt;right) {
                        // 情况2：叔节点是黑色且z是右孩子
                        z = z-&gt;parent;
                        leftRotate(z);
                    }
                    // 情况3：叔节点是黑色且z是左孩子
                    z-&gt;parent-&gt;color = Color::BLACK;
                    z-&gt;parent-&gt;parent-&gt;color = Color::RED;
                    rightRotate(z-&gt;parent-&gt;parent);
                }
            } else {
                // 对称情况
                auto y = z-&gt;parent-&gt;parent-&gt;left;
                if (y-&gt;color == Color::RED) {
                    z-&gt;parent-&gt;color = Color::BLACK;
                    y-&gt;color = Color::BLACK;
                    z-&gt;parent-&gt;parent-&gt;color = Color::RED;
                    z = z-&gt;parent-&gt;parent;
                } else {
                    if (z == z-&gt;parent-&gt;left) {
                        z = z-&gt;parent;
                        rightRotate(z);
                    }
                    z-&gt;parent-&gt;color = Color::BLACK;
                    z-&gt;parent-&gt;parent-&gt;color = Color::RED;
                    leftRotate(z-&gt;parent-&gt;parent);
                }
            }
        }
        root-&gt;color = Color::BLACK;
    }

    // 中序遍历辅助函数
    void inOrderHelper(std::shared_ptr&lt;RBTreeNode&gt; node, std::vector&lt;int&gt;&amp; result) const {
        if (node != nil) {
            inOrderHelper(node-&gt;left, result);
            result.push_back(node-&gt;val);
            inOrderHelper(node-&gt;right, result);
        }
    }

public:
    // 构造函数
    RBTree() {
        nil = std::make_shared&lt;RBTreeNode&gt;(0);
        nil-&gt;color = Color::BLACK;
        root = nil;
    }

    // 插入节点
    void insert(int val) {
        auto z = std::make_shared&lt;RBTreeNode&gt;(val);
        auto y = nil;
        auto x = root;

        while (x != nil) {
            y = x;
            if (z-&gt;val &lt; x-&gt;val) {
                x = x-&gt;left;
            } else {
                x = x-&gt;right;
            }
        }

        z-&gt;parent = y;
        if (y == nil) {
            root = z;
        } else if (z-&gt;val &lt; y-&gt;val) {
            y-&gt;left = z;
        } else {
            y-&gt;right = z;
        }

        z-&gt;left = nil;
        z-&gt;right = nil;
        z-&gt;color = Color::RED;
        insertFixup(z);
    }

    // 中序遍历
    std::vector&lt;int&gt; inOrderTraversal() const {
        std::vector&lt;int&gt; result;
        inOrderHelper(root, result);
        return result;
    }
};

/**
 * 基于红黑树的树形排序
 * @param arr 待排序数组
 */
void rbTreeSort(std::vector&lt;int&gt;&amp; arr) {
    RBTree tree;

    // 构建红黑树
    for (int num : arr) {
        tree.insert(num);
    }

    // 中序遍历获取排序结果
    arr = tree.inOrderTraversal();
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {5, 3, 7, 2, 4, 6, 8, 1, 9, 0};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用红黑树排序
    rbTreeSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 性能测试
    std::vector&lt;int&gt; largeArr(10000);
    std::generate(largeArr.begin(), largeArr.end(), []() {
        return rand() % 100000;
    });

    std::cout &lt;&lt; "对10000个元素进行排序..." &lt;&lt; std::endl;
    rbTreeSort(largeArr);
    std::cout &lt;&lt; "排序完成，检查前10个结果: ";
    for (int i = 0; i &lt; 10; ++i) {
        std::cout &lt;&lt; largeArr[i] &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
