/**
 * 鸡尾酒排序实现
 * @param arr 待排序的数组引用
 */
void cocktailSort(std::vector&lt;int&gt;&amp; arr) {
    bool swapped = true;  // 标记是否发生交换
    int start = 0;        // 未排序部分的起始索引
    int end = arr.size() - 1; // 未排序部分的结束索引

    // 只要在上一轮中有交换发生，就继续排序
    while (swapped) {
        swapped = false; // 重置交换标志

        // 从左到右的遍历（类似冒泡排序）
        for (int i = start; i &lt; end; ++i) {
            if (arr[i] &gt; arr[i + 1]) {
                std::swap(arr[i], arr[i + 1]);
                swapped = true; // 发生了交换
            }
        }

        // 如果没有发生交换，说明数组已经有序
        if (!swapped) {
            break;
        }

        swapped = false; // 重置交换标志
        --end; // 最右侧元素已就位，缩小未排序范围

        // 从右到左的遍历（反向冒泡）
        for (int i = end - 1; i &gt;= start; --i) {
            if (arr[i] &gt; arr[i + 1]) {
                std::swap(arr[i], arr[i + 1]);
                swapped = true; // 发生了交换
            }
        }

        ++start; // 最左侧元素已就位，缩小未排序范围
    }
}

int main() {
    // 测试数据
    std::vector&lt;int&gt; arr = {5, 1, 4, 2, 8, 0, 2};

    // 打印排序前的数组
    std::cout &lt;&lt; "排序前: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    // 调用鸡尾酒排序
    cocktailSort(arr);

    // 打印排序后的数组
    std::cout &lt;&lt; "排序后: ";
    for (int num : arr) {
        std::cout &lt;&lt; num &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}
