using namespace std;

struct dsu {
    vector&lt;size_t&gt; pa, size, sum;

    explicit dsu(size_t size_)
        : pa(size_ * 2), size(size_ * 2, 1), sum(size_ * 2) {
        // size 与 sum 的前半段其实没有使用，只是为了让下标计算更简单
        iota(pa.begin(), pa.begin() + size_, size_);
        iota(pa.begin() + size_, pa.end(), size_);
        iota(sum.begin() + size_, sum.end(), 0);
    }

    void unite(size_t x, size_t y) {
        x = find(x), y = find(y);
        if (x == y) return;
        if (size[x] &lt; size[y]) swap(x, y);
        pa[y] = x;
        size[x] += size[y];
        sum[x] += sum[y];
    }

    void move(size_t x, size_t y) {
        auto fx = find(x), fy = find(y);
        if (fx == fy) return;
        pa[x] = fy;
        --size[fx], ++size[fy];
        sum[fx] -= x, sum[fy] += x;
    }

    size_t find(size_t x) { return pa[x] == x ? x : pa[x] = find(pa[x]); }
    };

    int main() {
    size_t n, m, op, x, y;
    while (cin &gt;&gt; n &gt;&gt; m) {
        dsu dsu(n + 1);  // 元素范围是 1..n
        while (m--) {
        cin &gt;&gt; op;
        switch (op) {
            case 1:
            cin &gt;&gt; x &gt;&gt; y;
            dsu.unite(x, y);
            break;
            case 2:
            cin &gt;&gt; x &gt;&gt; y;
            dsu.move(x, y);
            break;
            case 3:
            cin &gt;&gt; x;
            x = dsu.find(x);
            cout &lt;&lt; dsu.size[x] &lt;&lt; ' ' &lt;&lt; dsu.sum[x] &lt;&lt; '\n';
            break;
            default:
            assert(false);  // not reachable
        }
        }
    }
}
</code></pre> Python<pre><code>class Dsu:
    def __init__(self, size):
        # size 与 sum 的前半段其实没有使用，只是为了让下标计算更简单
        self.pa = list(range(size, size * 2)) * 2
        self.size = [1] * size * 2
        self.sum = list(range(size)) * 2

    def union(self, x, y):
        x, y = self.find(x), self.find(y)
        if x == y:
            return
        if self.size[x] &lt; self.size[y]:
            x, y = y, x
        self.pa[y] = x
        self.size[x] += self.size[y]
        self.sum[x] += self.sum[y]

    def move(self, x, y):
        fx, fy = self.find(x), self.find(y)
        if fx == fy:
            return
        self.pa[x] = fy
        self.size[fx] -= 1
        self.size[fy] += 1
        self.sum[fx] -= x
        self.sum[fy] += x

    def find(self, x):
        if self.pa[x] != x:
            self.pa[x] = self.find(self.pa[x])
        return self.pa[x]


