                if (word2[i-1] == word1[j-1]) 
                    cur[j] = pre[j-1];
                else 
                    cur[j] = 1 + min({pre[j-1], pre[j], cur[j-1]});
            }
            pre = cur;
        }

        return pre[m];
    }
};
