C++<pre><code>class Solution {
private:
    const int N = 110, M = 6010;
    vector&lt;int&gt; he, e, ne, w;
    vector&lt;int&gt; dist;
    vector&lt;bool&gt; vis;
    const int INF = 0x3f3f3f3f;
    int n, k, idx = 0;

    // 添加边
    void add(int a, int b, int c) {
        e.push_back(b);
        ne.push_back(he[a]);
        he[a] = idx;
        w.push_back(c);
        idx++;
    }

public:
    int networkDelayTime(vector&lt;vector&lt;int&gt;&gt;&amp; ts, int n, int k) {
        // 初始化邻接表
        he = vector&lt;int&gt;(n + 1, -1);
        e.clear();
        ne.clear();
        w.clear();
        idx = 0;

        // 添加边
        for (const auto&amp; t : ts) {
            int u = t[0], v = t[1], c = t[2];
            add(u, v, c);
        }

        // 执行SPFA算法
        spfa();

        // 找出最大最短距离
        int ans = 0;
        for (int i = 1; i &lt;= n; i++) {
            ans = max(ans, dist[i]);
        }

        return ans &gt; INF / 2 ? -1 : ans;
    }

private:
    void spfa() {
        // 初始化距离和访问数组
        dist = vector&lt;int&gt;(n + 1, INF);
        vis = vector&lt;bool&gt;(n + 1, false);

        // 起始节点距离设为0
        dist[k] = 0;

        // 使用队列进行松弛
        deque&lt;int&gt; d;
        d.push_back(k);
        vis[k] = true;

        while (!d.empty()) {
            // 取出队首节点
            int poll = d.front();
            d.pop_front();
            vis[poll] = false;

            // 遍历当前节点的所有邻接节点
            for (int i = he[poll]; i != -1; i = ne[i]) {
                int j = e[i];

                // 尝试松弛边
                if (dist[j] &gt; dist[poll] + w[i]) {
                    dist[j] = dist[poll] + w[i];

                    // 如果节点已在队列中，跳过
                    if (vis[j]) continue;

                    // 将节点加入队列
                    d.push_back(j);
                    vis[j] = true;
                }
            }
        }
    }
};
</code></pre> <p>基础知识： Bellman-Ford 算法（最短路径，解决负权边，检测负环回路）+ 队列优化</p> C++<pre><code>#include &lt;iostream&gt;  
#include &lt;vector&gt;  
#include &lt;limits&gt;  
#include &lt;algorithm&gt;  

// 定义边的结构体  
struct Edge {  
    int src;    // 起点  
    int dest;   // 终点  
    int weight; // 权重  
};  

class BellmanFord {  
private:  
    int V;              // 顶点数  
    std::vector&lt;Edge&gt; edges; // 边的集合  

public:  
    // 构造函数  
    BellmanFord(int vertices) : V(vertices) {}  

    // 添加边  
    void addEdge(int src, int dest, int weight) {  
        edges.push_back({src, dest, weight});  
    }  

    // Bellman-Ford算法主体  
    std::pair&lt;bool, std::vector&lt;int&gt;&gt; findShortestPaths(int source) {  
        // 距离数组，初始化为正无穷  
        std::vector&lt;int&gt; dist(V, std::numeric_limits&lt;int&gt;::max());  

        // 源点距离为0  
        dist[source] = 0;  

        // 记录前驱节点的数组  
        std::vector&lt;int&gt; predecessor(V, -1);  

        // 松弛操作V-1次  
        for (int i = 1; i &lt;= V - 1; ++i) {  
            bool updated = false;  

            // 遍历所有边进行松弛  
            for (const auto&amp; edge : edges) {  
                int u = edge.src;  
                int v = edge.dest;  
                int weight = edge.weight;  

                // 避免整数溢出  
                if (dist[u] != std::numeric_limits&lt;int&gt;::max() &amp;&amp;  
                    dist[u] + weight &lt; dist[v]) {  
                    dist[v] = dist[u] + weight;  
                    predecessor[v] = u;  
                    updated = true;  
                }  
            }  

            // 如果没有更新，提前退出  
            if (!updated) break;  
        }  

        // 检测负权环  
        for (const auto&amp; edge : edges) {  
            int u = edge.src;  
            int v = edge.dest;  
            int weight = edge.weight;  

            // 如果还能继续松弛，说明存在负权环  
            if (dist[u] != std::numeric_limits&lt;int&gt;::max() &amp;&amp;  
                dist[u] + weight &lt; dist[v]) {  
                return {false, {}}; // 存在负权环  
            }  
        }  

        return {true, dist};  
    }  

    // 重建最短路径  
    std::vector&lt;int&gt; getPath(const std::vector&lt;int&gt;&amp; predecessor, int target) {  
        std::vector&lt;int&gt; path;  
        for (int v = target; v != -1; v = predecessor[v]) {  
            path.push_back(v);  
        }  
        std::reverse(path.begin(), path.end());  
        return path;  
    }  
};  

// 主函数示例  
int main() {  
    // 创建图：5个顶点  
    BellmanFord graph(5);  

    // 添加边   
    // 0: A, 1: B, 2: C, 3: D, 4: E  
    graph.addEdge(0, 1, 3);  // A -&gt; B, 权重3  
    graph.addEdge(0, 2, 2);  // A -&gt; C, 权重2  
    graph.addEdge(1, 3, 4);  // B -&gt; D, 权重4  
    graph.addEdge(3, 2, -1); // D -&gt; C, 权重-1  

    // 以A(0)为源点求最短路径  
    int source = 0;  
    auto [success, distances] = graph.findShortestPaths(source);  

    if (success) {  
        std::cout &lt;&lt; "最短路径距离:\n";  
        for (int i = 0; i &lt; distances.size(); ++i) {  
            std::cout &lt;&lt; "从源点到顶点 " &lt;&lt; i &lt;&lt; ": "   
                      &lt;&lt; (distances[i] == std::numeric_limits&lt;int&gt;::max() ?   
                          "不可达" : std::to_string(distances[i])) &lt;&lt; "\n";  
        }  
    } else {  
        std::cout &lt;&lt; "图中存在负权环\n";  
    }  

    return 0;  
}  
