Java<pre><code>import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);

        // 题目未指明是否有多组测试用例，但示例代码有while循环，我们遵循它
        // 实际比赛中根据题目要求确定
        while (in.hasNextInt()) {
            int n = in.nextInt();
            int a = in.nextInt();
            int b = in.nextInt();
            int c = in.nextInt();
            int[] s = new int[n];
            for (int i = 0; i &lt; n; i++) {
                s[i] = in.nextInt();
            }

            // dp[i][j] 表示消除区间 [i, j] 的最大得分
            int[][] dp = new int[n][n];

            // 按照区间长度 len 从小到大进行 DP
            for (int len = 1; len &lt;= n; len++) {
                // i 是区间的起始点
                for (int i = 0; i &lt;= n - len; i++) {
                    // j 是区间的结束点
                    int j = i + len - 1;

                    // Base Case: 区间长度为 1
                    if (len == 1) {
                        dp[i][j] = a;
                        continue;
                    }

                    // --- 状态转移 ---

                    // 1. 分割点策略：将 [i, j] 分为 [i, k] 和 [k+1, j]
                    for (int k = i; k &lt; j; k++) {
                        dp[i][j] = Math.max(dp[i][j], dp[i][k] + dp[k+1][j]);
                    }

                    // 2. 组合消除策略：考虑 s[i] 和 s[j]
                    if (s[i] == s[j]) {
                        // 2.1 二消：消除 s[i] 和 s[j]
                        // 需要先消除中间的 [i+1, j-1]
                        // 如果 len=2, i+1 &gt; j-1, 中间为空,得分为0
                        int middleScoreForTwo = (len == 2) ? 0 : dp[i+1][j-1];
                        dp[i][j] = Math.max(dp[i][j], middleScoreForTwo + b);

                        // 2.2 三消：消除 s[i], s[k], s[j]
                        // 遍历中间所有可能的 s[k]
                        for (int k = i + 1; k &lt; j; k++) {
                            if (s[k] == s[i]) {
                                // 需要先消除 [i+1, k-1] 和 [k+1, j-1]
                                int part1Score = (i + 1 &gt; k - 1) ? 0 : dp[i+1][k-1];
                                int part2Score = (k + 1 &gt; j - 1) ? 0 : dp[k+1][j-1];
                                dp[i][j] = Math.max(dp[i][j], part1Score + part2Score + c);
                            }
                        }
                    }
                }
            }

            System.out.println(dp[0][n-1]);
        }

        in.close();
    }
}
